import { motion } from 'framer-motion'
import './StatsCard.css'

export default function StatsCard({ title, value, icon, trend, trendValue, color = 'blue' }) {
  const isPositive = trend === 'up'

  return (
    <motion.div
      className={`stats-card stats-card-${color}`}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      whileHover={{ y: -4 }}
    >
      <div className="stats-header">
        <div className="stats-info">
          <p className="stats-title">{title}</p>
          <h3 className="stats-value">{value}</h3>
        </div>
        <div className="stats-icon">{icon}</div>
      </div>

      {trendValue && (
        <div className={`stats-trend ${isPositive ? 'positive' : 'negative'}`}>
          <span className="trend-icon">{isPositive ? '‚Üë' : '‚Üì'}</span>
          <span className="trend-value">{trendValue}</span>
          <span className="trend-label">vs last month</span>
        </div>
      )}
    </motion.div>
  )
}

import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'
import useAdminStore from '../../store/adminStore'
import Input from '../../components/common/Input'
import Button from '../../components/common/Button'
import useToast from '../../hooks/useToast'
import { authAPI } from '../../services/api'
import './AdminLogin.css'

export default function AdminLogin() {
  const navigate = useNavigate()
  const { toast } = useToast()
  const { setAdmin } = useAdminStore()
  
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  })
  const [loading, setLoading] = useState(false)

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)

    try {
      const response = await authAPI.login(formData)
      const { user, token } = response.data

      // TEMPORARILY DISABLED: Admin role check
      // Any user can access admin panel for testing
      /*
      if (user.role !== 'admin' && user.role !== 'sub_admin') {
        toast.error('Access denied. Admin credentials required.')
        setLoading(false)
        return
      }
      */

      setAdmin(user, token)
      toast.success(`Welcome back, ${user.name}!`)
      navigate('/admin/dashboard')
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Invalid credentials')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="admin-login-page">
      <motion.div
        className="admin-login-container"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <div className="admin-login-header">
          <div className="admin-logo">
            <span className="logo-icon">üëë</span>
            <h1>MegaArtsStore</h1>
          </div>
          <h2>Admin Dashboard</h2>
          <p>Sign in to access the admin panel</p>
        </div>

        <form onSubmit={handleSubmit} className="admin-login-form">
          <Input
            label="Email Address"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="admin@megaartsstore.com"
            required
            autoFocus
          />

          <Input
            label="Password"
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter your password"
            required
          />

          <Button
            type="submit"
            variant="primary"
            size="large"
            loading={loading}
            className="admin-login-btn"
          >
            Sign In
          </Button>
        </form>

        <div className="admin-login-footer">
          <p>Admin access only ‚Ä¢ Unauthorized access is prohibited</p>
        </div>
      </motion.div>
    </div>
  )
}

import { useState } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { couponsAPI } from '../../services/api'
import { StatusBadge } from '../../components/common/Badge'
import Modal from '../../components/common/Modal'
import Button from '../../components/common/Button'
import useToast from '../../hooks/useToast'
import './CouponsManagement.css'

export default function CouponsManagement() {
  const [showForm, setShowForm] = useState(false)
  const [editingCoupon, setEditingCoupon] = useState(null)
  const { toast } = useToast()
  const queryClient = useQueryClient()

  const { data, isLoading } = useQuery({
    queryKey: ['admin-coupons'],
    queryFn: () => couponsAPI.getAll?.()
  })

  const createMutation = useMutation({
    mutationFn: (data) => couponsAPI.create?.(data),
    onSuccess: () => {
      queryClient.invalidateQueries(['admin-coupons'])
      toast.success('Coupon created successfully')
      setShowForm(false)
    },
    onError: () => toast.error('Failed to create coupon')
  })

  const updateMutation = useMutation({
    mutationFn: ({ id, data }) => couponsAPI.update?.(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries(['admin-coupons'])
      toast.success('Coupon updated')
      setShowForm(false)
      setEditingCoupon(null)
    },
    onError: () => toast.error('Failed to update coupon')
  })

  const toggleMutation = useMutation({
    mutationFn: ({ id, isActive }) => couponsAPI.toggle?.(id, isActive),
    onSuccess: () => {
      queryClient.invalidateQueries(['admin-coupons'])
      toast.success('Coupon status updated')
    }
  })

  const coupons = data?.data || []

  const handleEdit = (coupon) => {
    setEditingCoupon(coupon)
    setShowForm(true)
  }

  const handleCloseForm = () => {
    setShowForm(false)
    setEditingCoupon(null)
  }

  return (
    <div className="coupons-management">
      <div className="page-header">
        <div>
          <h1>Coupons Management</h1>
          <p>Create and manage discount codes</p>
        </div>
        <Button variant="primary" onClick={() => setShowForm(true)}>
          + Create Coupon
        </Button>
      </div>

      {/* Stats */}
      <div className="coupons-stats">
        <div className="stat-card">
          <span className="stat-icon">üéüÔ∏è</span>
          <div>
            <span className="stat-value">{coupons.length}</span>
            <span className="stat-label">Total Coupons</span>
          </div>
        </div>
        <div className="stat-card">
          <span className="stat-icon">‚úÖ</span>
          <div>
            <span className="stat-value">{coupons.filter(c => c.is_active).length}</span>
            <span className="stat-label">Active</span>
          </div>
        </div>
        <div className="stat-card">
          <span className="stat-icon">üìä</span>
          <div>
            <span className="stat-value">{coupons.reduce((sum, c) => sum + (c.usage_count || 0), 0)}</span>
            <span className="stat-label">Total Uses</span>
          </div>
        </div>
      </div>

      {/* Coupons Grid */}
      {isLoading ? (
        <div className="loading-state">Loading coupons...</div>
      ) : coupons.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">üéüÔ∏è</div>
          <h3>No coupons yet</h3>
          <p>Create your first discount coupon</p>
          <Button variant="primary" onClick={() => setShowForm(true)}>
            Create Coupon
          </Button>
        </div>
      ) : (
        <div className="coupons-grid">
          {coupons.map((coupon, index) => (
            <motion.div
              key={coupon.id}
              className={`coupon-card ${!coupon.is_active ? 'inactive' : ''}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.05 }}
            >
              <div className="coupon-header">
                <span className="coupon-code">{coupon.code}</span>
                <StatusBadge status={coupon.is_active ? 'active' : 'inactive'} />
              </div>

              <div className="coupon-discount">
                {coupon.discount_type === 'percentage' 
                  ? `${coupon.discount_value}% OFF`
                  : `‚Çπ${coupon.discount_value} OFF`
                }
              </div>

              <div className="coupon-details">
                <div className="detail">
                  <span className="label">Min Order</span>
                  <span className="value">‚Çπ{coupon.min_order_value || 0}</span>
                </div>
                <div className="detail">
                  <span className="label">Uses</span>
                  <span className="value">{coupon.usage_count || 0}/{coupon.max_uses || '‚àû'}</span>
                </div>
                <div className="detail">
                  <span className="label">Expires</span>
                  <span className="value">
                    {coupon.expires_at 
                      ? new Date(coupon.expires_at).toLocaleDateString('en-IN') 
                      : 'Never'
                    }
                  </span>
                </div>
              </div>

              <div className="coupon-actions">
                <button className="action-btn edit" onClick={() => handleEdit(coupon)}>
                  Edit
                </button>
                <button 
                  className={`action-btn ${coupon.is_active ? 'disable' : 'enable'}`}
                  onClick={() => toggleMutation.mutate({ 
                    id: coupon.id, 
                    isActive: !coupon.is_active 
                  })}
                >
                  {coupon.is_active ? 'Disable' : 'Enable'}
                </button>
              </div>
            </motion.div>
          ))}
        </div>
      )}

      {/* Create/Edit Modal */}
      <Modal
        isOpen={showForm}
        onClose={handleCloseForm}
        title={editingCoupon ? 'Edit Coupon' : 'Create Coupon'}
        size="medium"
      >
        <CouponForm
          initialValues={editingCoupon}
          onSubmit={(data) => {
            if (editingCoupon) {
              updateMutation.mutate({ id: editingCoupon.id, data })
            } else {
              createMutation.mutate(data)
            }
          }}
          onCancel={handleCloseForm}
          loading={createMutation.isPending || updateMutation.isPending}
        />
      </Modal>
    </div>
  )
}

function CouponForm({ initialValues, onSubmit, onCancel, loading }) {
  const [formData, setFormData] = useState({
    code: initialValues?.code || '',
    discount_type: initialValues?.discount_type || 'percentage',
    discount_value: initialValues?.discount_value || '',
    min_order_value: initialValues?.min_order_value || '',
    max_uses: initialValues?.max_uses || '',
    expires_at: initialValues?.expires_at?.split('T')[0] || ''
  })

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData(prev => ({ ...prev, [name]: value }))
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    onSubmit({
      ...formData,
      code: formData.code.toUpperCase(),
      discount_value: parseFloat(formData.discount_value),
      min_order_value: formData.min_order_value ? parseFloat(formData.min_order_value) : null,
      max_uses: formData.max_uses ? parseInt(formData.max_uses) : null,
      expires_at: formData.expires_at || null
    })
  }

  return (
    <form className="coupon-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="code">Coupon Code</label>
        <input
          id="code"
          name="code"
          type="text"
          value={formData.code}
          onChange={handleChange}
          placeholder="e.g., SUMMER20"
          className="form-input"
          required
          style={{ textTransform: 'uppercase' }}
        />
      </div>

      <div className="form-row">
        <div className="form-group">
          <label htmlFor="discount_type">Discount Type</label>
          <select
            id="discount_type"
            name="discount_type"
            value={formData.discount_type}
            onChange={handleChange}
            className="form-select"
          >
            <option value="percentage">Percentage (%)</option>
            <option value="fixed">Fixed Amount (‚Çπ)</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="discount_value">Discount Value</label>
          <input
            id="discount_value"
            name="discount_value"
            type="number"
            value={formData.discount_value}
            onChange={handleChange}
            placeholder={formData.discount_type === 'percentage' ? '20' : '500'}
            className="form-input"
            required
            min={1}
            max={formData.discount_type === 'percentage' ? 100 : undefined}
          />
        </div>
      </div>

      <div className="form-row">
        <div className="form-group">
          <label htmlFor="min_order_value">Min Order Value (‚Çπ)</label>
          <input
            id="min_order_value"
            name="min_order_value"
            type="number"
            value={formData.min_order_value}
            onChange={handleChange}
            placeholder="Optional"
            className="form-input"
            min={0}
          />
        </div>

        <div className="form-group">
          <label htmlFor="max_uses">Max Uses</label>
          <input
            id="max_uses"
            name="max_uses"
            type="number"
            value={formData.max_uses}
            onChange={handleChange}
            placeholder="Unlimited"
            className="form-input"
            min={1}
          />
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="expires_at">Expiry Date</label>
        <input
          id="expires_at"
          name="expires_at"
          type="date"
          value={formData.expires_at}
          onChange={handleChange}
          className="form-input"
          min={new Date().toISOString().split('T')[0]}
        />
      </div>

      <div className="form-actions">
        <Button type="button" variant="secondary" onClick={onCancel}>
          Cancel
        </Button>
        <Button type="submit" variant="primary" loading={loading}>
          {initialValues ? 'Update Coupon' : 'Create Coupon'}
        </Button>
      </div>
    </form>
  )
}

import { useState } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { productsAPI } from '../../services/api'
import { StatusBadge } from '../../components/common/Badge'
import useToast from '../../hooks/useToast'
import './InventoryManagement.css'

export default function InventoryManagement() {
  const [searchQuery, setSearchQuery] = useState('')
  const [filterStock, setFilterStock] = useState('all')
  const { toast } = useToast()
  const queryClient = useQueryClient()

  const { data, isLoading } = useQuery({
    queryKey: ['inventory'],
    queryFn: () => productsAPI.getAll?.()
  })

  const updateStockMutation = useMutation({
    mutationFn: ({ productId, stock }) => productsAPI.updateStock?.(productId, stock),
    onSuccess: () => {
      queryClient.invalidateQueries(['inventory'])
      toast.success('Stock updated successfully')
    },
    onError: () => toast.error('Failed to update stock')
  })

  const products = data?.data || []
  
  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name?.toLowerCase().includes(searchQuery.toLowerCase())
    
    if (filterStock === 'low') {
      return matchesSearch && product.stock <= 10 && product.stock > 0
    } else if (filterStock === 'out') {
      return matchesSearch && product.stock === 0
    } else if (filterStock === 'in') {
      return matchesSearch && product.stock > 10
    }
    return matchesSearch
  })

  const getStockStatus = (stock) => {
    if (stock === 0) return 'out-of-stock'
    if (stock <= 10) return 'low-stock'
    return 'in-stock'
  }

  // Summary stats
  const totalProducts = products.length
  const lowStockCount = products.filter(p => p.stock <= 10 && p.stock > 0).length
  const outOfStockCount = products.filter(p => p.stock === 0).length
  const totalValue = products.reduce((sum, p) => sum + (p.price * p.stock), 0)

  return (
    <div className="inventory-management">
      <div className="page-header">
        <h1>Inventory Management</h1>
        <p>Track and manage product stock levels</p>
      </div>

      {/* Stats */}
      <div className="inventory-stats">
        <div className="stat-card">
          <span className="stat-icon">üì¶</span>
          <div>
            <span className="stat-value">{totalProducts}</span>
            <span className="stat-label">Total Products</span>
          </div>
        </div>
        <div className="stat-card warning">
          <span className="stat-icon">‚ö†Ô∏è</span>
          <div>
            <span className="stat-value">{lowStockCount}</span>
            <span className="stat-label">Low Stock</span>
          </div>
        </div>
        <div className="stat-card danger">
          <span className="stat-icon">‚ùå</span>
          <div>
            <span className="stat-value">{outOfStockCount}</span>
            <span className="stat-label">Out of Stock</span>
          </div>
        </div>
        <div className="stat-card">
          <span className="stat-icon">üí∞</span>
          <div>
            <span className="stat-value">‚Çπ{(totalValue / 100000).toFixed(1)}L</span>
            <span className="stat-label">Total Value</span>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="filters-bar">
        <div className="search-box">
          <span className="search-icon">üîç</span>
          <input
            type="text"
            placeholder="Search products..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <div className="filter-tabs">
          {[
            { key: 'all', label: 'All' },
            { key: 'in', label: 'In Stock' },
            { key: 'low', label: 'Low Stock' },
            { key: 'out', label: 'Out of Stock' }
          ].map(({ key, label }) => (
            <button
              key={key}
              className={filterStock === key ? 'active' : ''}
              onClick={() => setFilterStock(key)}
            >
              {label}
            </button>
          ))}
        </div>
      </div>

      {/* Inventory Table */}
      <div className="table-container">
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Product</th>
              <th>SKU</th>
              <th>Category</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {isLoading ? (
              Array.from({ length: 5 }).map((_, i) => (
                <tr key={i} className="skeleton-row">
                  <td colSpan={7}><div className="skeleton" /></td>
                </tr>
              ))
            ) : filteredProducts.length === 0 ? (
              <tr>
                <td colSpan={7} className="empty-row">
                  No products found
                </td>
              </tr>
            ) : (
              filteredProducts.map((product, index) => (
                <motion.tr
                  key={product.id}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.02 }}
                  className={getStockStatus(product.stock)}
                >
                  <td>
                    <div className="product-cell">
                      <img 
                        src={product.images?.[0] || '/placeholder.jpg'} 
                        alt={product.name}
                        className="product-thumb"
                      />
                      <span className="product-name">{product.name}</span>
                    </div>
                  </td>
                  <td className="sku">
                    {product.sku || `SKU-${product.id?.slice(-6).toUpperCase()}`}
                  </td>
                  <td>{product.category}</td>
                  <td className="price">‚Çπ{product.price?.toLocaleString()}</td>
                  <td>
                    <StockInput 
                      value={product.stock} 
                      onChange={(newStock) => 
                        updateStockMutation.mutate({ 
                          productId: product.id, 
                          stock: newStock 
                        })
                      }
                    />
                  </td>
                  <td>
                    <StatusBadge status={getStockStatus(product.stock)} />
                  </td>
                  <td>
                    <button className="action-btn restock">
                      Restock
                    </button>
                  </td>
                </motion.tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  )
}

// Stock Input Component
function StockInput({ value, onChange }) {
  const [editing, setEditing] = useState(false)
  const [inputValue, setInputValue] = useState(value)

  const handleSave = () => {
    const newValue = parseInt(inputValue, 10)
    if (!isNaN(newValue) && newValue >= 0) {
      onChange(newValue)
    } else {
      setInputValue(value)
    }
    setEditing(false)
  }

  if (editing) {
    return (
      <div className="stock-input-edit">
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onBlur={handleSave}
          onKeyDown={(e) => e.key === 'Enter' && handleSave()}
          autoFocus
          min={0}
        />
      </div>
    )
  }

  return (
    <button 
      className={`stock-value ${value <= 10 ? 'low' : ''} ${value === 0 ? 'out' : ''}`}
      onClick={() => setEditing(true)}
    >
      {value}
    </button>
  )
}

import { useState } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { ordersAPI } from '../../services/api'
import { StatusBadge } from '../../components/common/Badge'
import Modal from '../../components/common/Modal'
import Button from '../../components/common/Button'
import { TableRowSkeleton } from '../../components/common/Skeleton'
import useToast from '../../hooks/useToast'
import './OrdersManagement.css'

export default function OrdersManagement() {
  const [selectedOrder, setSelectedOrder] = useState(null)
  const [filterStatus, setFilterStatus] = useState('all')
  const [searchQuery, setSearchQuery] = useState('')
  const { toast } = useToast()
  const queryClient = useQueryClient()

  const { data, isLoading } = useQuery({
    queryKey: ['admin-orders', filterStatus],
    queryFn: () => ordersAPI.getAll?.({ status: filterStatus !== 'all' ? filterStatus : undefined })
  })

  const updateStatusMutation = useMutation({
    mutationFn: ({ orderId, status }) => ordersAPI.updateStatus?.(orderId, status),
    onSuccess: () => {
      queryClient.invalidateQueries(['admin-orders'])
      toast.success('Order status updated')
      setSelectedOrder(null)
    },
    onError: () => toast.error('Failed to update order status')
  })

  const orders = data?.data || []
  const filteredOrders = orders.filter(order => 
    order.id?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    order.customer_name?.toLowerCase().includes(searchQuery.toLowerCase())
  )

  const statusOptions = ['pending', 'processing', 'shipped', 'delivered', 'cancelled']

  return (
    <div className="orders-management">
      <div className="page-header">
        <h1>Orders Management</h1>
        <p>Manage and track customer orders</p>
      </div>

      {/* Filters */}
      <div className="filters-bar">
        <div className="search-box">
          <span className="search-icon">üîç</span>
          <input
            type="text"
            placeholder="Search orders..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <div className="filter-tabs">
          {['all', ...statusOptions].map(status => (
            <button
              key={status}
              className={filterStatus === status ? 'active' : ''}
              onClick={() => setFilterStatus(status)}
            >
              {status.charAt(0).toUpperCase() + status.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Orders Table */}
      <div className="table-container">
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Items</th>
              <th>Total</th>
              <th>Status</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {isLoading ? (
              Array.from({ length: 5 }).map((_, i) => (
                <TableRowSkeleton key={i} columns={7} />
              ))
            ) : filteredOrders.length === 0 ? (
              <tr>
                <td colSpan={7} className="empty-row">
                  No orders found
                </td>
              </tr>
            ) : (
              filteredOrders.map((order, index) => (
                <motion.tr
                  key={order.id}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.03 }}
                >
                  <td className="order-id">#{order.id?.slice(-8).toUpperCase()}</td>
                  <td>
                    <div className="customer-info">
                      <span className="customer-name">{order.customer_name || 'N/A'}</span>
                      <span className="customer-email">{order.customer_email}</span>
                    </div>
                  </td>
                  <td>{order.items?.length || 0} items</td>
                  <td className="order-total">‚Çπ{order.total?.toLocaleString()}</td>
                  <td><StatusBadge status={order.status} /></td>
                  <td className="order-date">
                    {new Date(order.created_at).toLocaleDateString('en-IN', {
                      day: 'numeric',
                      month: 'short',
                      year: 'numeric'
                    })}
                  </td>
                  <td>
                    <div className="action-buttons">
                      <button 
                        className="action-btn view"
                        onClick={() => setSelectedOrder(order)}
                      >
                        View
                      </button>
                    </div>
                  </td>
                </motion.tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Order Details Modal */}
      <Modal
        isOpen={!!selectedOrder}
        onClose={() => setSelectedOrder(null)}
        title={`Order #${selectedOrder?.id?.slice(-8).toUpperCase()}`}
        size="large"
      >
        {selectedOrder && (
          <div className="order-details-modal">
            {/* Status Update */}
            <div className="status-update-section">
              <label>Update Status:</label>
              <div className="status-buttons">
                {statusOptions.map(status => (
                  <button
                    key={status}
                    className={`status-btn ${selectedOrder.status === status ? 'active' : ''}`}
                    onClick={() => updateStatusMutation.mutate({ 
                      orderId: selectedOrder.id, 
                      status 
                    })}
                    disabled={updateStatusMutation.isPending}
                  >
                    {status.charAt(0).toUpperCase() + status.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            {/* Customer Info */}
            <div className="detail-section">
              <h4>Customer Information</h4>
              <p><strong>Name:</strong> {selectedOrder.customer_name}</p>
              <p><strong>Email:</strong> {selectedOrder.customer_email}</p>
              <p><strong>Phone:</strong> {selectedOrder.shipping_address?.phone}</p>
            </div>

            {/* Shipping Address */}
            <div className="detail-section">
              <h4>Shipping Address</h4>
              <p>
                {selectedOrder.shipping_address?.addressLine1}<br />
                {selectedOrder.shipping_address?.city}, {selectedOrder.shipping_address?.state} {selectedOrder.shipping_address?.pincode}
              </p>
            </div>

            {/* Order Items */}
            <div className="detail-section">
              <h4>Order Items</h4>
              <div className="order-items-list">
                {selectedOrder.items?.map((item, index) => (
                  <div key={index} className="order-item">
                    <img src={item.image || '/placeholder.jpg'} alt={item.name} />
                    <div className="item-details">
                      <span className="item-name">{item.name}</span>
                      <span className="item-meta">
                        Qty: {item.quantity} {item.size && `‚Ä¢ Size: ${item.size}`}
                      </span>
                    </div>
                    <span className="item-price">‚Çπ{(item.price * item.quantity).toLocaleString()}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* Order Summary */}
            <div className="order-summary-section">
              <div className="summary-row">
                <span>Subtotal</span>
                <span>‚Çπ{selectedOrder.subtotal?.toLocaleString()}</span>
              </div>
              <div className="summary-row">
                <span>Shipping</span>
                <span>{selectedOrder.shipping === 0 ? 'FREE' : `‚Çπ${selectedOrder.shipping}`}</span>
              </div>
              {selectedOrder.discount > 0 && (
                <div className="summary-row discount">
                  <span>Discount</span>
                  <span>-‚Çπ{selectedOrder.discount?.toLocaleString()}</span>
                </div>
              )}
              <div className="summary-row total">
                <span>Total</span>
                <strong>‚Çπ{selectedOrder.total?.toLocaleString()}</strong>
              </div>
            </div>
          </div>
        )}
      </Modal>
    </div>
  )
}

import { useState } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { reviewsAPI } from '../../services/api'
import { StatusBadge } from '../../components/common/Badge'
import StarRating from '../../components/common/StarRating'
import Modal from '../../components/common/Modal'
import Button from '../../components/common/Button'
import useToast from '../../hooks/useToast'
import './ReviewsModeration.css'

export default function ReviewsModeration() {
  const [filterStatus, setFilterStatus] = useState('pending')
  const [selectedReview, setSelectedReview] = useState(null)
  const { toast } = useToast()
  const queryClient = useQueryClient()

  const { data, isLoading } = useQuery({
    queryKey: ['admin-reviews', filterStatus],
    queryFn: () => reviewsAPI.getAll?.({ status: filterStatus !== 'all' ? filterStatus : undefined })
  })

  const moderateMutation = useMutation({
    mutationFn: ({ reviewId, action }) => reviewsAPI.moderate?.(reviewId, action),
    onSuccess: (_, { action }) => {
      queryClient.invalidateQueries(['admin-reviews'])
      toast.success(`Review ${action === 'approve' ? 'approved' : 'rejected'}`)
      setSelectedReview(null)
    },
    onError: () => toast.error('Failed to moderate review')
  })

  const reviews = data?.data || []

  const handleModerate = (reviewId, action) => {
    moderateMutation.mutate({ reviewId, action })
  }

  // Stats
  const pendingCount = reviews.filter(r => r.status === 'pending').length
  const approvedCount = reviews.filter(r => r.status === 'approved').length
  const rejectedCount = reviews.filter(r => r.status === 'rejected').length

  return (
    <div className="reviews-moderation">
      <div className="page-header">
        <h1>Reviews Moderation</h1>
        <p>Review and moderate customer feedback</p>
      </div>

      {/* Stats */}
      <div className="reviews-stats">
        <div className="stat-card warning">
          <span className="stat-value">{pendingCount}</span>
          <span className="stat-label">Pending</span>
        </div>
        <div className="stat-card success">
          <span className="stat-value">{approvedCount}</span>
          <span className="stat-label">Approved</span>
        </div>
        <div className="stat-card danger">
          <span className="stat-value">{rejectedCount}</span>
          <span className="stat-label">Rejected</span>
        </div>
      </div>

      {/* Filter Tabs */}
      <div className="filter-tabs">
        {[
          { key: 'pending', label: 'Pending', count: pendingCount },
          { key: 'approved', label: 'Approved' },
          { key: 'rejected', label: 'Rejected' },
          { key: 'all', label: 'All' }
        ].map(({ key, label, count }) => (
          <button
            key={key}
            className={filterStatus === key ? 'active' : ''}
            onClick={() => setFilterStatus(key)}
          >
            {label}
            {count > 0 && <span className="count">{count}</span>}
          </button>
        ))}
      </div>

      {/* Reviews List */}
      {isLoading ? (
        <div className="loading-state">Loading reviews...</div>
      ) : reviews.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">‚≠ê</div>
          <h3>No reviews found</h3>
          <p>No reviews match the current filter</p>
        </div>
      ) : (
        <div className="reviews-list">
          {reviews.map((review, index) => (
            <motion.div
              key={review.id}
              className="review-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.03 }}
            >
              <div className="review-header">
                <div className="review-product">
                  <img 
                    src={review.product?.images?.[0] || '/placeholder.jpg'} 
                    alt={review.product?.name}
                    className="product-thumb"
                  />
                  <div>
                    <span className="product-name">{review.product?.name || 'Unknown Product'}</span>
                    <span className="customer-name">by {review.user?.name || 'Anonymous'}</span>
                  </div>
                </div>
                <StatusBadge status={review.status} />
              </div>

              <div className="review-content">
                <div className="review-rating">
                  <StarRating rating={review.rating} size="small" />
                  <span className="rating-value">({review.rating}/5)</span>
                </div>
                {review.title && <h4 className="review-title">{review.title}</h4>}
                <p className="review-comment">{review.comment}</p>
                <span className="review-date">
                  {new Date(review.created_at).toLocaleDateString('en-IN', {
                    day: 'numeric',
                    month: 'short',
                    year: 'numeric'
                  })}
                </span>
              </div>

              {review.status === 'pending' && (
                <div className="review-actions">
                  <Button 
                    variant="primary" 
                    size="small"
                    onClick={() => handleModerate(review.id, 'approve')}
                    loading={moderateMutation.isPending}
                  >
                    Approve
                  </Button>
                  <Button 
                    variant="secondary" 
                    size="small"
                    onClick={() => setSelectedReview(review)}
                  >
                    View Full
                  </Button>
                  <button 
                    className="reject-btn"
                    onClick={() => handleModerate(review.id, 'reject')}
                  >
                    Reject
                  </button>
                </div>
              )}
            </motion.div>
          ))}
        </div>
      )}

      {/* Review Detail Modal */}
      <Modal
        isOpen={!!selectedReview}
        onClose={() => setSelectedReview(null)}
        title="Review Details"
        size="medium"
      >
        {selectedReview && (
          <div className="review-detail-modal">
            <div className="modal-product">
              <img 
                src={selectedReview.product?.images?.[0] || '/placeholder.jpg'} 
                alt={selectedReview.product?.name}
              />
              <div>
                <h4>{selectedReview.product?.name}</h4>
                <p>‚Çπ{selectedReview.product?.price?.toLocaleString()}</p>
              </div>
            </div>

            <div className="modal-reviewer">
              <h5>Reviewer</h5>
              <p>{selectedReview.user?.name} ({selectedReview.user?.email})</p>
            </div>

            <div className="modal-rating">
              <StarRating rating={selectedReview.rating} />
            </div>

            {selectedReview.title && (
              <div className="modal-title">
                <h5>Title</h5>
                <p>{selectedReview.title}</p>
              </div>
            )}

            <div className="modal-comment">
              <h5>Review</h5>
              <p>{selectedReview.comment}</p>
            </div>

            {selectedReview.status === 'pending' && (
              <div className="modal-actions">
                <Button 
                  variant="primary"
                  onClick={() => handleModerate(selectedReview.id, 'approve')}
                  loading={moderateMutation.isPending}
                >
                  Approve Review
                </Button>
                <Button 
                  variant="secondary"
                  onClick={() => handleModerate(selectedReview.id, 'reject')}
                >
                  Reject Review
                </Button>
              </div>
            )}
          </div>
        )}
      </Modal>
    </div>
  )
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import Modal from '../common/Modal'
import Button from '../common/Button'
import AddressForm from '../checkout/AddressForm'
import useToast from '../../hooks/useToast'
import './AddressBook.css'

export default function AddressBook({ addresses = [], onAdd, onEdit, onDelete, onSetDefault }) {
  const [showForm, setShowForm] = useState(false)
  const [editingAddress, setEditingAddress] = useState(null)
  const { toast } = useToast()

  const handleSubmit = (formData) => {
    if (editingAddress) {
      onEdit?.(editingAddress.id, formData)
      toast.success('Address updated successfully')
    } else {
      onAdd?.(formData)
      toast.success('Address added successfully')
    }
    setShowForm(false)
    setEditingAddress(null)
  }

  const handleEdit = (address) => {
    setEditingAddress(address)
    setShowForm(true)
  }

  const handleDelete = (addressId) => {
    if (window.confirm('Are you sure you want to delete this address?')) {
      onDelete?.(addressId)
      toast.success('Address deleted')
    }
  }

  return (
    <div className="address-book">
      <div className="address-book-header">
        <h3>Saved Addresses</h3>
        <Button variant="primary" onClick={() => setShowForm(true)}>
          + Add New Address
        </Button>
      </div>

      {addresses.length === 0 ? (
        <div className="addresses-empty">
          <div className="empty-icon">üìç</div>
          <h4>No addresses saved</h4>
          <p>Add a delivery address to make checkout faster!</p>
        </div>
      ) : (
        <div className="addresses-grid">
          {addresses.map((address, index) => (
            <motion.div
              key={address.id}
              className={`address-card ${address.isDefault ? 'default' : ''}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.05 }}
            >
              {address.isDefault && (
                <span className="default-badge">Default</span>
              )}

              <div className="address-content">
                <strong className="address-name">{address.fullName}</strong>
                <p className="address-text">
                  {address.addressLine1}
                  {address.addressLine2 && <><br />{address.addressLine2}</>}
                  <br />
                  {address.city}, {address.state} {address.pincode}
                </p>
                <p className="address-phone">üìû {address.phone}</p>
              </div>

              <div className="address-actions">
                <button className="action-btn" onClick={() => handleEdit(address)}>
                  Edit
                </button>
                <button 
                  className="action-btn" 
                  onClick={() => handleDelete(address.id)}
                >
                  Delete
                </button>
                {!address.isDefault && (
                  <button 
                    className="action-btn set-default"
                    onClick={() => onSetDefault?.(address.id)}
                  >
                    Set as Default
                  </button>
                )}
              </div>
            </motion.div>
          ))}
        </div>
      )}

      {/* Add/Edit Address Modal */}
      <Modal
        isOpen={showForm}
        onClose={() => {
          setShowForm(false)
          setEditingAddress(null)
        }}
        title={editingAddress ? 'Edit Address' : 'Add New Address'}
        size="medium"
      >
        <AddressForm
          initialValues={editingAddress || {}}
          onSubmit={handleSubmit}
          onCancel={() => {
            setShowForm(false)
            setEditingAddress(null)
          }}
          submitLabel={editingAddress ? 'Update Address' : 'Add Address'}
        />
      </Modal>
    </div>
  )
}

import { useState } from 'react'
import { useQuery } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { ordersAPI } from '../../services/api'
import { StatusBadge } from '../common/Badge'
import Modal from '../common/Modal'
import Button from '../common/Button'
import './OrderHistory.css'

export default function OrderHistory() {
  const [selectedOrder, setSelectedOrder] = useState(null)
  
  const { data, isLoading } = useQuery({
    queryKey: ['order-history'],
    queryFn: () => ordersAPI.getHistory()
  })

  const orders = data?.data || []

  if (isLoading) {
    return (
      <div className="orders-loading">
        <div className="spinner"></div>
        <p>Loading orders...</p>
      </div>
    )
  }

  if (orders.length === 0) {
    return (
      <div className="orders-empty">
        <div className="empty-icon">üì¶</div>
        <h3>No orders yet</h3>
        <p>Start shopping to see your orders here!</p>
        <Button variant="primary" onClick={() => window.location.href = '/products'}>
          Browse Products
        </Button>
      </div>
    )
  }

  return (
    <div className="order-history">
      <div className="orders-list">
        {orders.map((order, index) => (
          <motion.div
            key={order.id}
            className="order-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.05 }}
          >
            <div className="order-header">
              <div>
                <span className="order-id">Order #{order.id?.slice(-8)}</span>
                <span className="order-date">
                  {new Date(order.created_at).toLocaleDateString('en-IN', {
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric'
                  })}
                </span>
              </div>
              <StatusBadge status={order.status} />
            </div>

            <div className="order-items-preview">
              {order.items?.slice(0, 3).map((item, i) => (
                <div key={i} className="preview-item">
                  <img src={item.image || '/placeholder.jpg'} alt={item.name} />
                </div>
              ))}
              {order.items?.length > 3 && (
                <div className="more-items">+{order.items.length - 3}</div>
              )}
            </div>

            <div className="order-summary">
              <div className="order-total">
                <span>Total</span>
                <strong className="gradient-text">‚Çπ{order.total?.toLocaleString()}</strong>
              </div>
              <div className="order-actions">
                <Button 
                  variant="secondary" 
                  size="small"
                  onClick={() => setSelectedOrder(order)}
                >
                  View Details
                </Button>
                {order.status === 'delivered' && (
                  <Button variant="primary" size="small">
                    Reorder
                  </Button>
                )}
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Order Details Modal */}
      <Modal 
        isOpen={!!selectedOrder} 
        onClose={() => setSelectedOrder(null)}
        title={`Order #${selectedOrder?.id?.slice(-8)}`}
        size="large"
      >
        {selectedOrder && <OrderDetails order={selectedOrder} />}
      </Modal>
    </div>
  )
}

function OrderDetails({ order }) {
  return (
    <div className="order-details">
      {/* Status Timeline */}
      <div className="order-timeline">
        <TimelineStep 
          status="completed" 
          label="Order Placed" 
          date={order.created_at}
        />
        <TimelineStep 
          status={['processing', 'shipped', 'delivered'].includes(order.status) ? 'completed' : 'pending'} 
          label="Processing" 
        />
        <TimelineStep 
          status={['shipped', 'delivered'].includes(order.status) ? 'completed' : 'pending'} 
          label="Shipped" 
        />
        <TimelineStep 
          status={order.status === 'delivered' ? 'completed' : 'pending'} 
          label="Delivered" 
        />
      </div>

      {/* Items */}
      <div className="order-items-list">
        <h4>Items</h4>
        {order.items?.map((item, index) => (
          <div key={index} className="order-item">
            <img src={item.image || '/placeholder.jpg'} alt={item.name} />
            <div className="item-info">
              <span className="item-name">{item.name}</span>
              {item.size && <span className="item-size">Size: {item.size}</span>}
              <span className="item-qty">Qty: {item.quantity}</span>
            </div>
            <span className="item-price">‚Çπ{(item.price * item.quantity).toLocaleString()}</span>
          </div>
        ))}
      </div>

      {/* Shipping Address */}
      <div className="shipping-info">
        <h4>Shipping Address</h4>
        <p>
          {order.shipping_address?.fullName}<br />
          {order.shipping_address?.addressLine1}<br />
          {order.shipping_address?.city}, {order.shipping_address?.state} {order.shipping_address?.pincode}<br />
          Phone: {order.shipping_address?.phone}
        </p>
      </div>

      {/* Payment Info */}
      <div className="payment-info">
        <h4>Payment</h4>
        <div className="payment-row">
          <span>Subtotal</span>
          <span>‚Çπ{order.subtotal?.toLocaleString()}</span>
        </div>
        <div className="payment-row">
          <span>Tax</span>
          <span>‚Çπ{order.tax?.toLocaleString()}</span>
        </div>
        <div className="payment-row">
          <span>Shipping</span>
          <span>{order.shipping === 0 ? 'FREE' : `‚Çπ${order.shipping}`}</span>
        </div>
        {order.discount > 0 && (
          <div className="payment-row discount">
            <span>Discount</span>
            <span>-‚Çπ{order.discount?.toLocaleString()}</span>
          </div>
        )}
        <div className="payment-row total">
          <span>Total</span>
          <strong>‚Çπ{order.total?.toLocaleString()}</strong>
        </div>
      </div>
    </div>
  )
}

function TimelineStep({ status, label, date }) {
  return (
    <div className={`timeline-step ${status}`}>
      <div className="timeline-dot" />
      <div className="timeline-content">
        <span className="timeline-label">{label}</span>
        {date && (
          <span className="timeline-date">
            {new Date(date).toLocaleDateString('en-IN', { month: 'short', day: 'numeric' })}
          </span>
        )}
      </div>
    </div>
  )
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import Button from '../common/Button'
import './ARCompare.css'

export default function ARCompare({ screenshots = [], onClose }) {
  const [selectedImages, setSelectedImages] = useState([screenshots[0], screenshots[1]].filter(Boolean))

  const toggleImage = (image) => {
    if (selectedImages.includes(image)) {
      setSelectedImages(selectedImages.filter(img => img !== image))
    } else if (selectedImages.length < 2) {
      setSelectedImages([...selectedImages, image])
    }
  }

  return (
    <div className="ar-compare">
      <div className="compare-header">
        <h3>Compare AR Try-Ons</h3>
        <button className="close-btn" onClick={onClose}>√ó</button>
      </div>

      {/* Comparison View */}
      <div className="comparison-view">
        {selectedImages.length === 2 ? (
          <>
            <div className="compare-image">
              <img src={selectedImages[0].url} alt="Comparison 1" />
              <span className="image-label">Image 1</span>
            </div>
            <div className="vs-divider">VS</div>
            <div className="compare-image">
              <img src={selectedImages[1].url} alt="Comparison 2" />
              <span className="image-label">Image 2</span>
            </div>
          </>
        ) : (
          <div className="select-prompt">
            <p>Select 2 images to compare</p>
            <p className="prompt-hint">
              {selectedImages.length === 0 ? 'Choose your first image' : 'Choose your second image'}
            </p>
          </div>
        )}
      </div>

      {/* Image Selector */}
      <div className="image-selector">
        <p className="selector-label">Select images ({selectedImages.length}/2)</p>
        <div className="selector-grid">
          {screenshots.map((screenshot, index) => (
            <motion.div
              key={screenshot.id || index}
              className={`selector-item ${selectedImages.includes(screenshot) ? 'selected' : ''}`}
              onClick={() => toggleImage(screenshot)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <img src={screenshot.url} alt={`Option ${index + 1}`} />
              {selectedImages.includes(screenshot) && (
                <div className="selected-badge">
                  {selectedImages.indexOf(screenshot) + 1}
                </div>
              )}
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  )
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import Modal from '../common/Modal'
import Button from '../common/Button'
import useToast from '../../hooks/useToast'
import './ShareModal.css'

export default function ShareModal({ image, onClose }) {
  const { toast } = useToast()
  const [copying, setCopying] = useState(false)

  const shareOptions = [
    { name: 'WhatsApp', icon: 'üí¨', color: '#25D366', action: 'whatsapp' },
    { name: 'Facebook', icon: 'üìò', color: '#1877F2', action: 'facebook' },
    { name: 'Twitter', icon: 'üê¶', color: '#1DA1F2', action: 'twitter' },
    { name: 'Instagram', icon: 'üì∑', color: '#E4405F', action: 'instagram' },
  ]

  const handleShare = async (platform) => {
    const shareUrl = window.location.href
    const shareText = 'Check out how this looks on me! üíç'

    const urls = {
      whatsapp: `https://wa.me/?text=${encodeURIComponent(shareText + ' ' + shareUrl)}`,
      facebook: `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(shareUrl)}`,
      twitter: `https://twitter.com/intent/tweet?text=${encodeURIComponent(shareText)}&url=${encodeURIComponent(shareUrl)}`,
      instagram: shareUrl // Instagram doesn't support direct sharing via URL
    }

    if (platform === 'instagram') {
      toast.info('Save the image and share it on Instagram!')
      return
    }

    window.open(urls[platform], '_blank', 'width=600,height=400')
    toast.success(`Sharing to ${platform}!`)
  }

  const handleCopyLink = async () => {
    setCopying(true)
    try {
      await navigator.clipboard.writeText(window.location.href)
      toast.success('Link copied to clipboard!')
    } catch (error) {
      toast.error('Failed to copy link')
    } finally {
      setCopying(false)
    }
  }

  const handleNativeShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: 'My AR Try-On',
          text: 'Check out how this looks on me!',
          url: window.location.href
        })
        toast.success('Shared successfully!')
      } catch (error) {
        if (error.name !== 'AbortError') {
          toast.error('Failed to share')
        }
      }
    } else {
      toast.info('Sharing not supported on this device')
    }
  }

  return (
    <Modal isOpen onClose={onClose} size="medium">
      <div className="share-modal">
        <h3>Share Your AR Try-On</h3>

        <div className="preview-image">
          <img src={image?.url} alt="AR Try-on" />
        </div>

        <div className="share-options">
          {shareOptions.map((option) => (
            <motion.button
              key={option.action}
              className="share-option"
              onClick={() => handleShare(option.action)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span className="option-icon" style={{ background: option.color }}>
                {option.icon}
              </span>
              <span className="option-name">{option.name}</span>
            </motion.button>
          ))}
        </div>

        <div className="share-actions">
          <Button
            variant="secondary"
            onClick={handleCopyLink}
            loading={copying}
          >
            üìã Copy Link
          </Button>
          
          {navigator.share && (
            <Button variant="primary" onClick={handleNativeShare}>
              üì§ Share
            </Button>
          )}
        </div>
      </div>
    </Modal>
  )
}

import { Component } from 'react'
import './ErrorBoundary.css'

class ErrorBoundary extends Component {
  constructor(props) {
    super(props)
    this.state = { hasError: false, error: null, errorInfo: null }
  }

  static getDerivedStateFromError(error) {
    return { hasError: true }
  }

  componentDidCatch(error, errorInfo) {
    this.setState({ error, errorInfo })
    // Log error to reporting service (e.g., Sentry)
    console.error('Error caught by boundary:', error, errorInfo)
    
    // You can send to error tracking service here
    // Example: logErrorToService(error, errorInfo)
  }

  handleReset = () => {
    this.setState({ hasError: false, error: null, errorInfo: null })
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-boundary">
          <div className="error-content">
            <div className="error-icon">‚ö†Ô∏è</div>
            <h2>Something went wrong</h2>
            <p>We're sorry, but something unexpected happened. Please try refreshing the page.</p>
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
              <button 
                className="btn btn-primary"
                onClick={() => window.location.reload()}
              >
                Refresh Page
              </button>
              {this.props.fallbackComponent && (
                <button 
                  className="btn btn-secondary"
                  onClick={this.handleReset}
                >
                  Try Again
                </button>
              )}
            </div>
            {import.meta.env.DEV && this.state.error && (
              <details className="error-details">
                <summary>Error Details (Development Only)</summary>
                <pre>{this.state.error.toString()}</pre>
                <pre>{this.state.errorInfo?.componentStack}</pre>
              </details>
            )}
          </div>
        </div>
      )
    }

    return this.props.children
  }
}

export default ErrorBoundary

import { useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import './Modal.css'

export default function Modal({ 
  isOpen, 
  onClose, 
  title, 
  children, 
  size = 'medium',
  showCloseButton = true 
}) {
  // Close on escape key
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose()
    }
    
    if (isOpen) {
      document.addEventListener('keydown', handleEscape)
      document.body.style.overflow = 'hidden'
    }
    
    return () => {
      document.removeEventListener('keydown', handleEscape)
      document.body.style.overflow = 'unset'
    }
  }, [isOpen, onClose])

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div 
          className="modal-overlay"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
        >
          <motion.div 
            className={`modal-content modal-${size}`}
            initial={{ opacity: 0, scale: 0.9, y: 20 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.9, y: 20 }}
            onClick={(e) => e.stopPropagation()}
          >
            {(title || showCloseButton) && (
              <div className="modal-header">
                {title && <h2 className="modal-title font-display">{title}</h2>}
                {showCloseButton && (
                  <button className="modal-close" onClick={onClose}>
                    ‚úï
                  </button>
                )}
              </div>
            )}
            <div className="modal-body">
              {children}
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  )
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import './StarRating.css'

export default function StarRating({ 
  rating = 0, 
  maxStars = 5, 
  size = 'medium',
  interactive = false,
  onChange = null,
  showCount = false,
  count = 0
}) {
  const [hoverRating, setHoverRating] = useState(0)
  const [selectedRating, setSelectedRating] = useState(rating)

  const handleClick = (value) => {
    if (!interactive) return
    setSelectedRating(value)
    onChange?.(value)
  }

  const handleMouseEnter = (value) => {
    if (!interactive) return
    setHoverRating(value)
  }

  const handleMouseLeave = () => {
    if (!interactive) return
    setHoverRating(0)
  }

  const displayRating = interactive ? (hoverRating || selectedRating) : rating

  return (
    <div className={`star-rating ${size} ${interactive ? 'interactive' : ''}`}>
      <div className="stars">
        {[...Array(maxStars)].map((_, index) => {
          const starValue = index + 1
          const isFilled = starValue <= displayRating
          const isPartial = !isFilled && starValue - 0.5 <= displayRating

          return (
            <motion.button
              key={index}
              type="button"
              className={`star ${isFilled ? 'filled' : ''} ${isPartial ? 'partial' : ''}`}
              onClick={() => handleClick(starValue)}
              onMouseEnter={() => handleMouseEnter(starValue)}
              onMouseLeave={handleMouseLeave}
              disabled={!interactive}
              whileHover={interactive ? { scale: 1.1 } : {}}
              whileTap={interactive ? { scale: 0.95 } : {}}
            >
              {isPartial ? (
                <span className="star-icon">
                  <span className="star-half">‚òÖ</span>
                  <span className="star-empty">‚òÜ</span>
                </span>
              ) : (
                <span className="star-icon">{isFilled ? '‚òÖ' : '‚òÜ'}</span>
              )}
            </motion.button>
          )
        })}
      </div>
      {showCount && count > 0 && (
        <span className="rating-count">({count})</span>
      )}
    </div>
  )
}

