import { useState } from 'react'
import { Link } from 'react-router-dom'
import { motion } from 'framer-motion'
import Modal from '../common/Modal'
import Button from '../common/Button'
import { useCartStore } from '../../store/cartStore'
import { useWishlistStore } from '../../store/wishlistStore'
import useToast from '../../hooks/useToast'
import './QuickViewModal.css'

export default function QuickViewModal({ product, isOpen, onClose }) {
  const [selectedImage, setSelectedImage] = useState(0)
  const [selectedSize, setSelectedSize] = useState('')
  const [quantity, setQuantity] = useState(1)
  
  const { addItem } = useCartStore()
  const { items: wishlistItems, addItem: addToWishlist, removeItem: removeFromWishlist } = useWishlistStore()
  const { toast } = useToast()

  const isInWishlist = wishlistItems.some(item => item.id === product?.id)
  const images = product?.images || ['/placeholder.jpg']
  const sizes = product?.sizes || []

  const handleAddToCart = () => {
    if (sizes.length > 0 && !selectedSize) {
      toast.error('Please select a size')
      return
    }
    addItem({ ...product, quantity, size: selectedSize })
    toast.success(`${product.name} added to cart!`)
    onClose()
  }

  const handleToggleWishlist = () => {
    if (isInWishlist) {
      removeFromWishlist(product.id)
      toast.info('Removed from wishlist')
    } else {
      addToWishlist(product)
      toast.success('Added to wishlist!')
    }
  }

  if (!product) return null

  const finalPrice = product.discount 
    ? product.price * (1 - product.discount / 100) 
    : product.price

  return (
    <Modal isOpen={isOpen} onClose={onClose} size="large">
      <div className="quick-view-content">
        {/* Images */}
        <div className="quick-view-gallery">
          <motion.img 
            key={selectedImage}
            src={images[selectedImage]} 
            alt={product.name}
            className="quick-view-main-image"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          />
          {product.ar_enabled && (
            <span className="badge badge-gold quick-view-ar-badge">AR Available</span>
          )}
          {images.length > 1 && (
            <div className="quick-view-thumbnails">
              {images.map((img, index) => (
                <button
                  key={index}
                  className={`quick-view-thumb ${selectedImage === index ? 'active' : ''}`}
                  onClick={() => setSelectedImage(index)}
                >
                  <img src={img} alt="" />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Info */}
        <div className="quick-view-info">
          <h2 className="quick-view-title font-display">{product.name}</h2>
          <p className="quick-view-category">{product.category}</p>

          {/* Rating */}
          {product.avg_rating > 0 && (
            <div className="quick-view-rating">
              {'‚≠ê'.repeat(Math.round(product.avg_rating))}
              <span>({product.avg_rating.toFixed(1)})</span>
            </div>
          )}

          {/* Price */}
          <div className="quick-view-price">
            {product.discount > 0 ? (
              <>
                <span className="price-original">‚Çπ{product.price.toLocaleString()}</span>
                <span className="price-current gradient-text">‚Çπ{finalPrice.toLocaleString()}</span>
                <span className="badge badge-sale">-{product.discount}%</span>
              </>
            ) : (
              <span className="price-current gradient-text">‚Çπ{product.price.toLocaleString()}</span>
            )}
          </div>

          {/* Description */}
          <p className="quick-view-description">{product.description}</p>

          {/* Size Selector */}
          {sizes.length > 0 && (
            <div className="quick-view-sizes">
              <label>Size:</label>
              <div className="size-options">
                {sizes.map(size => (
                  <button
                    key={size}
                    className={`size-btn ${selectedSize === size ? 'active' : ''}`}
                    onClick={() => setSelectedSize(size)}
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Quantity */}
          <div className="quick-view-quantity">
            <label>Quantity:</label>
            <div className="quantity-controls">
              <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>‚àí</button>
              <span>{quantity}</span>
              <button onClick={() => setQuantity(quantity + 1)}>+</button>
            </div>
          </div>

          {/* Actions */}
          <div className="quick-view-actions">
            <Button variant="primary" onClick={handleAddToCart}>
              Add to Cart
            </Button>
            <Button variant="secondary" onClick={handleToggleWishlist}>
              {isInWishlist ? '‚ù§Ô∏è' : 'ü§ç'}
            </Button>
          </div>

          {/* View Full Details */}
          <Link to={`/product/${product.id}`} className="view-full-link" onClick={onClose}>
            View Full Details ‚Üí
          </Link>
        </div>
      </div>
    </Modal>
  )
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import Button from '../common/Button'
import { useCartStore } from '../../store/cartStore'
import useToast from '../../hooks/useToast'
import './ProductBundle.css'

export default function ProductBundle({ mainProduct, bundleProducts = [] }) {
  const { addItem } = useCartStore()
  const { toast } = useToast()
  const [selectedProducts, setSelectedProducts] = useState([mainProduct.id])

  const toggleProduct = (productId) => {
    if (productId === mainProduct.id) return // Can't deselect main product
    
    setSelectedProducts(prev =>
      prev.includes(productId)
        ? prev.filter(id => id !== productId)
        : [...prev, productId]
    )
  }

  const allProducts = [mainProduct, ...bundleProducts]
  const selectedItems = allProducts.filter(p => selectedProducts.includes(p.id))
  
  const totalPrice = selectedItems.reduce((sum, p) => sum + p.price, 0)
  const savings = totalPrice * 0.1 // 10% bundle discount
  const finalPrice = totalPrice - savings

  const handleAddBundle = () => {
    selectedItems.forEach(product => {
      addItem(product)
    })
    toast.success(`Added ${selectedItems.length} items to cart!`)
  }

  return (
    <motion.div
      className="product-bundle"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
    >
      <h3 className="bundle-title">Frequently Bought Together</h3>
      
      <div className="bundle-items">
        {allProducts.map((product, index) => (
          <div key={product.id} className="bundle-item-wrapper">
            {index > 0 && <div className="plus-icon">+</div>}
            <div
              className={`bundle-item ${selectedProducts.includes(product.id) ? 'selected' : ''}`}
              onClick={() => toggleProduct(product.id)}
            >
              <div className="item-image">
                <img src={product.images?.[0]} alt={product.name} />
                {product.id !== mainProduct.id && (
                  <div className="checkbox">
                    {selectedProducts.includes(product.id) ? '‚úì' : ''}
                  </div>
                )}
              </div>
              <div className="item-info">
                <p className="item-name">{product.name}</p>
                <p className="item-price">‚Çπ{product.price.toLocaleString()}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="bundle-summary">
        <div className="price-breakdown">
          <div className="price-row">
            <span>Total Price:</span>
            <span className="original-price">‚Çπ{totalPrice.toLocaleString()}</span>
          </div>
          <div className="price-row savings">
            <span>Bundle Savings (10%):</span>
            <span>-‚Çπ{savings.toLocaleString()}</span>
          </div>
          <div className="price-row total">
            <span>Final Price:</span>
            <span className="gradient-text">‚Çπ{finalPrice.toLocaleString()}</span>
          </div>
        </div>

        <Button
          variant="primary"
          size="large"
          onClick={handleAddBundle}
          className="add-bundle-btn"
        >
          Add {selectedItems.length} Items to Cart
        </Button>
      </div>
    </motion.div>
  )
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import Button from '../common/Button'
import StarRating from '../common/StarRating'
import { reviewsAPI } from '../../services/api'
import useToast from '../../hooks/useToast'
import './ReviewForm.css'

export default function ReviewForm({ productId, productName, onSuccess, onCancel }) {
  const [rating, setRating] = useState(0)
  const [title, setTitle] = useState('')
  const [comment, setComment] = useState('')
  const [loading, setLoading] = useState(false)
  const { toast } = useToast()

  const handleSubmit = async (e) => {
    e.preventDefault()
    
    if (rating === 0) {
      toast.error('Please select a rating')
      return
    }
    
    if (!comment.trim()) {
      toast.error('Please write a review')
      return
    }

    setLoading(true)
    try {
      await reviewsAPI.add(productId, {
        rating,
        title,
        comment
      })
      toast.success('Review submitted successfully!')
      onSuccess?.()
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Failed to submit review')
    } finally {
      setLoading(false)
    }
  }

  return (
    <motion.div 
      className="review-form"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
    >
      <h3>Write a Review for {productName}</h3>
      
      <form onSubmit={handleSubmit}>
        {/* Rating */}
        <div className="form-group">
          <label>Your Rating *</label>
          <StarRating 
            rating={rating} 
            onRatingChange={setRating}
            interactive
            size="large"
          />
        </div>

        {/* Title */}
        <div className="form-group">
          <label htmlFor="review-title">Review Title</label>
          <input
            id="review-title"
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Summarize your review"
            className="form-input"
          />
        </div>

        {/* Comment */}
        <div className="form-group">
          <label htmlFor="review-comment">Your Review *</label>
          <textarea
            id="review-comment"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Share your experience with this product..."
            rows={5}
            className="form-textarea"
            required
          />
          <span className="char-count">{comment.length}/500</span>
        </div>

        {/* Actions */}
        <div className="review-form-actions">
          {onCancel && (
            <Button type="button" variant="secondary" onClick={onCancel}>
              Cancel
            </Button>
          )}
          <Button type="submit" variant="primary" loading={loading}>
            Submit Review
          </Button>
        </div>
      </form>
    </motion.div>
  )
}

import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { motion } from 'framer-motion'
import Button from '../components/common/Button'
import './ARTryOn.css'

export default function ARTryOn() {
  const { id } = useParams()
  const navigate = useNavigate()
  const [cameraActive, setCameraActive] = useState(false)
  const [screenshot, setScreenshot] = useState(null)

  const handleStartAR = () => {
    // In production, this would activate the device camera
    // and overlay the 3D model using AR libraries
    setCameraActive(true)
  }

  const handleCapture = () => {
    // Mock screenshot capture
    setScreenshot('/ar-screenshot-demo.jpg')
  }

  const handleShare = () => {
    // Mock share functionality
    if (navigator.share) {
      navigator.share({
        title: 'My AR Try-On',
