            </motion.div>
          )}
        </div>
      )}
    </div>
  )
}

import { useState, useEffect } from 'react'
import { useNavigate, useSearchParams, Link } from 'react-router-dom'
import { motion } from 'framer-motion'
import { useAuthStore } from '../store/authStore'
import { useWishlistStore } from '../store/wishlistStore'
import Button from '../components/common/Button'
import OrderHistory from '../components/account/OrderHistory'
import AddressBook from '../components/account/AddressBook'
import ProfileForm from '../components/account/ProfileForm'
import ProductCard from '../components/products/ProductCard'
import useToast from '../hooks/useToast'
import './Account.css'

export default function Account() {
  const navigate = useNavigate()
  const [searchParams, setSearchParams] = useSearchParams()
  const { toast } = useToast()
  const { user, logout, isAuthenticated } = useAuthStore()
  const { items: wishlistItems, removeItem: removeFromWishlist } = useWishlistStore()
  
  const [activeTab, setActiveTab] = useState(searchParams.get('tab') || 'profile')
  const [addresses, setAddresses] = useState([])

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login')
    }
  }, [isAuthenticated, navigate])

  useEffect(() => {
    const tab = searchParams.get('tab')
    if (tab) setActiveTab(tab)
  }, [searchParams])

  const handleTabChange = (tab) => {
    setActiveTab(tab)
    setSearchParams({ tab })
  }

  const handleLogout = () => {
    logout()
    toast.success('Logged out successfully')
    navigate('/')
  }

  // Address handlers
  const handleAddAddress = (address) => {
    setAddresses(prev => [...prev, { ...address, id: Date.now().toString() }])
  }

  const handleEditAddress = (id, address) => {
    setAddresses(prev => prev.map(a => a.id === id ? { ...a, ...address } : a))
  }

  const handleDeleteAddress = (id) => {
    setAddresses(prev => prev.filter(a => a.id !== id))
  }

  const handleSetDefaultAddress = (id) => {
    setAddresses(prev => prev.map(a => ({ ...a, isDefault: a.id === id })))
    toast.success('Default address updated')
  }

  if (!user) return null

  const tabs = [
    { id: 'profile', label: 'Profile', icon: 'üë§' },
    { id: 'orders', label: 'Orders', icon: 'üì¶' },
    { id: 'wishlist', label: 'Wishlist', icon: '‚ù§Ô∏è', count: wishlistItems.length },
    { id: 'addresses', label: 'Addresses', icon: 'üìç' },
  ]

  return (
    <div className="account-page">
      <div className="account-container">
        {/* Sidebar */}
        <motion.div 
          className="account-sidebar"
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
        >
          <div className="user-info">
            <div className="user-avatar">
              {user.name?.charAt(0).toUpperCase() || 'U'}
            </div>
            <h3>{user.name}</h3>
            <p>{user.email}</p>
          </div>

          <nav className="account-nav">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                className={activeTab === tab.id ? 'active' : ''}
                onClick={() => handleTabChange(tab.id)}
              >
                <span className="tab-icon">{tab.icon}</span>
                <span>{tab.label}</span>
                {tab.count > 0 && (
                  <span className="tab-count">{tab.count}</span>
                )}
              </button>
            ))}
          </nav>

          <Button onClick={handleLogout} variant="secondary" className="logout-btn">
            Logout
          </Button>
        </motion.div>

        {/* Content */}
        <motion.div 
          className="account-content"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          {activeTab === 'profile' && (
            <div className="tab-content">
              <h2 className="tab-title font-display">My Profile</h2>
              <ProfileForm />
            </div>
          )}

          {activeTab === 'orders' && (
            <div className="tab-content">
              <h2 className="tab-title font-display">Order History</h2>
              <OrderHistory />
            </div>
          )}

          {activeTab === 'wishlist' && (
            <div className="tab-content">
              <h2 className="tab-title font-display">My Wishlist</h2>
              {wishlistItems.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">‚ù§Ô∏è</div>
                  <h3>Your wishlist is empty</h3>
                  <p>Save items you love by clicking the heart icon</p>
                  <Link to="/products">
                    <Button variant="primary">Browse Products</Button>
                  </Link>
                </div>
              ) : (
                <div className="wishlist-grid">
                  {wishlistItems.map((product, index) => (
                    <ProductCard key={product.id} product={product} index={index} />
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'addresses' && (
            <div className="tab-content">
              <h2 className="tab-title font-display">My Addresses</h2>
              <AddressBook
                addresses={addresses}
                onAdd={handleAddAddress}
                onEdit={handleEditAddress}
                onDelete={handleDeleteAddress}
                onSetDefault={handleSetDefaultAddress}
              />
            </div>
          )}
        </motion.div>
      </div>
    </div>
  )
}

import { useState } from 'react'
import { Link } from 'react-router-dom'
import { motion } from 'framer-motion'
import Button from '../components/common/Button'
import { authAPI } from '../services/api'
import useToast from '../hooks/useToast'
import './Auth.css'

export default function ForgotPassword() {
  const [email, setEmail] = useState('')
  const [loading, setLoading] = useState(false)
  const [sent, setSent] = useState(false)
  const { toast } = useToast()

  const handleSubmit = async (e) => {
    e.preventDefault()
    
    if (!email.trim()) {
      toast.error('Please enter your email')
      return
    }

    setLoading(true)
    try {
      await authAPI.forgotPassword?.(email) || Promise.resolve()
      setSent(true)
      toast.success('Reset link sent to your email!')
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Failed to send reset link')
    } finally {
      setLoading(false)
    }
  }

  if (sent) {
    return (
      <div className="auth-page">
        <motion.div 
          className="auth-container"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <div className="auth-success">
            <div className="success-icon">‚úâÔ∏è</div>
            <h2>Check Your Email</h2>
            <p>
              We've sent a password reset link to<br />
              <strong>{email}</strong>
            </p>
            <p className="auth-hint">
              Didn't receive the email? Check your spam folder or{' '}
              <button onClick={() => setSent(false)} className="link-btn">
                try again
              </button>
            </p>
            <Link to="/login">
              <Button variant="secondary">Back to Login</Button>
            </Link>
          </div>
        </motion.div>
      </div>
    )
  }

  return (
    <div className="auth-page">
      <motion.div 
        className="auth-container"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <div className="auth-header">
          <h1 className="font-display">Forgot Password?</h1>
          <p>Enter your email and we'll send you a reset link</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              className="form-input"
              required
            />
          </div>

          <Button type="submit" variant="primary" size="large" loading={loading}>
            Send Reset Link
          </Button>
        </form>

        <div className="auth-footer">
          <p>
            Remember your password?{' '}
            <Link to="/login" className="auth-link">Sign In</Link>
          </p>
        </div>
      </motion.div>
    </div>
  )
}

