        </div>
      </section>
    </div>
  )
}

import { useState } from 'react'
import { useNavigate, Link } from 'react-router-dom'
import { motion } from 'framer-motion'
import useAuthStore from '../store/authStore'
import Input from '../components/common/Input'
import Button from '../components/common/Button'
import useToast from '../hooks/useToast'
import { authAPI } from '../services/api'
import './Auth.css'

export default function Login() {
  const navigate = useNavigate()
  const { toast } = useToast()
  const { setUser } = useAuthStore()
  
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  })
  const [loading, setLoading] = useState(false)

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)

    try {
      const response = await authAPI.login(formData)
      const { user, token } = response.data

      setUser(user, token)
      toast.success(`Welcome back, ${user.name}!`)
      navigate('/')
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Invalid credentials')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="auth-page">
      <motion.div
        className="auth-container"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <div className="auth-header">
          <h1>Welcome Back</h1>
          <p>Sign in to your account</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <Input
            label="Email Address"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            autoFocus
          />

          <Input
            label="Password"
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />

          <Button
            type="submit"
            variant="primary"
            size="large"
            loading={loading}
          >
            Sign In
          </Button>
        </form>

        <div className="auth-footer">
          <p>Don't have an account? <Link to="/register">Sign up</Link></p>
        </div>
      </motion.div>
    </div>
  )
}

import { useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom'
import { useQuery } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { productsAPI, reviewsAPI, recommendationsAPI } from '../services/api'
import { useCartStore } from '../store/cartStore'
import { useWishlistStore } from '../store/wishlistStore'
import Button from '../components/common/Button'
import ProductCard from '../components/products/ProductCard'
import useToast from '../hooks/useToast'
import './ProductDetail.css'

export default function ProductDetail() {
  const { id } = useParams()
  const navigate = useNavigate()
  const { toast } = useToast()
  const { addItem } = useCartStore()
  const { items: wishlistItems, addItem: addToWishlist, removeItem: removeFromWishlist } = useWishlistStore()
  
  const [selectedImage, setSelectedImage] = useState(0)
  const [quantity, setQuantity] = useState(1)
  const [selectedSize, setSelectedSize] = useState('')

  const { data: productData, isLoading } = useQuery({
    queryKey: ['product', id],
    queryFn: () => productsAPI.getById(id)
  })

  const { data: reviewsData } = useQuery({
    queryKey: ['reviews', id],
    queryFn: () => reviewsAPI.getByProduct(id)
  })

  const { data: similarData } = useQuery({
    queryKey: ['similar', id],
    queryFn: () => recommendationsAPI.similar(id),
    enabled: !!productData
  })

  const product = productData?.data
  const reviews = reviewsData?.data || []
  const similarProducts = similarData?.data?.products || []
  const isInWishlist = wishlistItems.some(item => item.id === product?.id)

  const handleAddToCart = () => {
    if (!selectedSize && product?.sizes?.length > 0) {
      toast.error('Please select a size')
      return
    }
    
    addItem({ ...product, quantity, size: selectedSize })
    toast.success(`${product.name} added to cart!`)
  }

  const handleToggleWishlist = () => {
    if (isInWishlist) {
      removeFromWishlist(product.id)
      toast.info('Removed from wishlist')
    } else {
      addToWishlist(product)
      toast.success('Added to wishlist!')
    }
  }

  if (isLoading) {
    return (
      <div className="product-detail-loading">
        <div className="spinner-large"></div>
        <p>Loading product...</p>
      </div>
    )
  }

  if (!product) {
    return (
      <div className="product-not-found">
        <h2>Product not found</h2>
        <Button variant="primary" onClick={() => navigate('/products')}>
          Browse Products
        </Button>
      </div>
    )
  }

  const images = product.images || ['/placeholder.jpg']
  const sizes = product.sizes || []

  return (
    <div className="product-detail-page">
      <div className="container">
        {/* Breadcrumb */}
        <nav className="breadcrumb">
          <Link to="/">Home</Link>
          <span>/</span>
          <Link to="/products">Products</Link>
          <span>/</span>
          <Link to={`/products/${product.category}`}>{product.category}</Link>
          <span>/</span>
          <span>{product.name}</span>
        </nav>

        <div className="product-detail-grid">
          {/* Image Gallery */}
          <div className="product-gallery">
            <motion.div 
              className="main-image"
              key={selectedImage}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              {product.ar_enabled && (
                <span className="badge badge-gold ar-badge">AR Available</span>
              )}
              <img 
                src={images[selectedImage]} 
                alt={product.name}
                className="gallery-main-img"
              />
            </motion.div>
            
            <div className="thumbnail-grid">
              {images.map((img, index) => (
                <motion.button
                  key={index}
                  className={`thumbnail ${selectedImage === index ? 'active' : ''}`}
                  onClick={() => setSelectedImage(index)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <img src={img} alt={`${product.name} ${index + 1}`} />
                </motion.button>
              ))}
            </div>

            {/* 3D Model Viewer */}
            {product.model_3d_url && (
              <div className="model-viewer-container">
                <model-viewer
                  src={product.model_3d_url}
                  alt={product.name}
                  auto-rotate
                  camera-controls
                  shadow-intensity="1"
                  className="model-viewer"
                ></model-viewer>
              </div>
            )}
          </div>

          {/* Product Info */}
          <div className="product-info">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
            >
              <h1 className="product-title font-display">{product.name}</h1>
              <p className="product-category">{product.category}</p>

              {/* Rating */}
              {product.avg_rating > 0 && (
                <div className="product-rating-display">
                  <div className="stars">
                    {'‚≠ê'.repeat(Math.round(product.avg_rating))}
                  </div>
                  <span className="rating-text">
                    {product.avg_rating.toFixed(1)} ({reviews.length} reviews)
                  </span>
                </div>
              )}

              {/* Price */}
              <div className="product-price-section">
                {product.discount > 0 ? (
                  <>
                    <span className="price-original">‚Çπ{product.price.toLocaleString()}</span>
                    <span className="price-current gradient-text">
                      ‚Çπ{(product.price * (1 - product.discount / 100)).toLocaleString()}
                    </span>
                    <span className="badge badge-sale">-{product.discount}% OFF</span>
                  </>
                ) : (
                  <span className="price-current gradient-text">‚Çπ{product.price.toLocaleString()}</span>
                )}
              </div>

              {/* Description */}
              <p className="product-description">{product.description}</p>

              {/* Size Selector */}
              {sizes.length > 0 && (
                <div className="size-selector">
                  <h4>Select Size</h4>
                  <div className="size-options">
                    {sizes.map((size) => (
                      <button
                        key={size}
                        className={`size-btn ${selectedSize === size ? 'active' : ''}`}
                        onClick={() => setSelectedSize(size)}
                      >
                        {size}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Quantity Selector */}
              <div className="quantity-selector">
                <h4>Quantity</h4>
                <div className="quantity-controls">
                  <button 
                    className="qty-btn"
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  >
                    ‚àí
                  </button>
                  <span className="qty-value">{quantity}</span>
                  <button 
                    className="qty-btn"
                    onClick={() => setQuantity(quantity + 1)}
                  >
                    +
                  </button>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="product-actions">
                <Button 
                  variant="primary" 
                  size="large"
                  onClick={handleAddToCart}
                  className="add-to-cart-btn"
                >
                  Add to Cart
                </Button>
                <Button 
                  variant="secondary" 
                  size="large"
                  onClick={handleToggleWishlist}
                >
                  {isInWishlist ? '‚ù§Ô∏è Saved' : 'ü§ç Save'}
                </Button>
              </div>

              {/* AR Try-On Button */}
              {product.ar_enabled && (
                <Link to={`/ar-try-on/${product.id}`}>
                  <Button variant="dark" size="large" className="ar-try-btn">
                    üì± Try in AR
                  </Button>
                </Link>
              )}

              {/* Product Details */}
              <div className="product-specs">
                <h4>Product Details</h4>
                <div className="specs-grid">
                  {product.material && (
                    <div className="spec-item">
                      <span className="spec-label">Material:</span>
                      <span className="spec-value">{product.material}</span>
                    </div>
                  )}
                  {product.weight && (
                    <div className="spec-item">
                      <span className="spec-label">Weight:</span>
                      <span className="spec-value">{product.weight}g</span>
                    </div>
                  )}
                  <div className="spec-item">
                    <span className="spec-label">SKU:</span>
                    <span className="spec-value">{product.id}</span>
                  </div>
                </div>
              </div>
            </motion.div>
          </div>
        </div>

        {/* Reviews Section */}
        {reviews.length > 0 && (
          <section className="reviews-section">
            <h2 className="font-display">Customer Reviews</h2>
            <div className="reviews-grid">
              {reviews.slice(0, 6).map((review, index) => (
                <motion.div
                  key={review.id}
                  className="review-card"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ delay: index * 0.1 }}
                >
                  <div className="review-header">
                    <div>
                      <p className="review-author">{review.user_name || 'Anonymous'}</p>
                      <div className="review-stars">
                        {'‚≠ê'.repeat(review.rating)}
                      </div>
                    </div>
                    <span className="review-date">
                      {new Date(review.created_at).toLocaleDateString()}
                    </span>
                  </div>
                  <p className="review-comment">{review.comment}</p>
                </motion.div>
              ))}
            </div>
          </section>
        )}

        {/* Similar Products */}
        {similarProducts.length > 0 && (
          <section className="similar-products">
            <h2 className="font-display">You May Also Like</h2>
            <div className="similar-grid">
              {similarProducts.slice(0, 4).map((product, index) => (
                <ProductCard key={product.id} product={product} index={index} />
              ))}
            </div>
          </section>
        )}
      </div>
    </div>
  )
}

import { useState } from 'react'
import { useParams, useSearchParams } from 'react-router-dom'
import { useQuery } from '@tanstack/react-query'
import { motion } from 'framer-motion'
import { productsAPI } from '../services/api'
import ProductCard from '../components/products/ProductCard'
import Button from '../components/common/Button'
import './Products.css'

export default function Products() {
  const { category } = useParams()
  const [searchParams, setSearchParams] = useSearchParams()
  
  const [filters, setFilters] = useState({
    category: category || searchParams.get('category') || '',
    minPrice: searchParams.get('minPrice') || '',
    maxPrice: searchParams.get('maxPrice') || '',
    material: searchParams.get('material') || '',
    arEnabled: searchParams.get('arEnabled') === 'true',
    sort: searchParams.get('sort') || 'newest'
  })
  
  const [viewMode, setViewMode] = useState('grid')
  const [page, setPage] = useState(1)

  const { data, isLoading } = useQuery({
    queryKey: ['products', filters, page],
    queryFn: () => productsAPI.getAll({
      category: filters.category,
      min_price: filters.minPrice,
      max_price: filters.maxPrice,
      material: filters.material,
      ar_enabled: filters.arEnabled || undefined,
      sort: filters.sort,
      page,
      per_page: 12
    })
  })

  const handleFilterChange = (key, value) => {
    const newFilters = { ...filters, [key]: value }
    setFilters(newFilters)
    setPage(1)
    
    // Update URL params
    const params = new URLSearchParams()
    Object.entries(newFilters).forEach(([k, v]) => {
      if (v) params.set(k, v)
    })
    setSearchParams(params)
  }

  const clearFilters = () => {
    setFilters({
      category: '',
      minPrice: '',
      maxPrice: '',
      material: '',
      arEnabled: false,
      sort: 'newest'
    })
    setSearchParams({})
    setPage(1)
  }

  const products = data?.data?.products || []
  const totalPages = data?.data?.total_pages || 1

  return (
    <div className="products-page">
      <div className="container">
        {/* Header */}
        <motion.div 
          className="products-header"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <div>
            <h1 className="font-display">
              {category ? category.charAt(0).toUpperCase() + category.slice(1) : 'All Products'}
            </h1>
            <p className="products-count">
              {data?.data?.total || 0} products found
            </p>
          </div>
          
          <div className="view-toggle">
            <button 
              className={`view-btn ${viewMode === 'grid' ? 'active' : ''}`}
              onClick={() => setViewMode('grid')}
            >
              ‚äû
            </button>
            <button 
              className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
              onClick={() => setViewMode('list')}
            >
              ‚ò∞
            </button>
          </div>
        </motion.div>

        <div className="products-layout">
          {/* Filters Sidebar */}
          <motion.aside 
            className="filters-sidebar"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
          >
            <div className="filters-header">
              <h3>Filters</h3>
              <button className="clear-filters" onClick={clearFilters}>
                Clear All
              </button>
            </div>

            {/* Category Filter */}
            <div className="filter-group">
              <h4>Category</h4>
              <div className="filter-options">
                {['bangles', 'rings', 'necklaces', 'earrings'].map((cat) => (
                  <label key={cat} className="filter-option">
                    <input
                      type="radio"
                      name="category"
                      checked={filters.category === cat}
                      onChange={() => handleFilterChange('category', cat)}
                    />
                    <span>{cat.charAt(0).toUpperCase() + cat.slice(1)}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Price Range */}
            <div className="filter-group">
              <h4>Price Range</h4>
              <div className="price-inputs">
                <input
                  type="number"
                  placeholder="Min"
                  value={filters.minPrice}
                  onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                  className="price-input"
                />
                <span>-</span>
                <input
                  type="number"
                  placeholder="Max"
                  value={filters.maxPrice}
                  onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                  className="price-input"
                />
              </div>
            </div>

            {/* Material Filter */}
            <div className="filter-group">
              <h4>Material</h4>
              <div className="filter-options">
                {['gold', 'silver', 'platinum', 'diamond'].map((mat) => (
                  <label key={mat} className="filter-option">
                    <input
                      type="radio"
                      name="material"
                      checked={filters.material === mat}
                      onChange={() => handleFilterChange('material', mat)}
                    />
                    <span>{mat.charAt(0).toUpperCase() + mat.slice(1)}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* AR Enabled */}
            <div className="filter-group">
              <label className="filter-option">
                <input
                  type="checkbox"
                  checked={filters.arEnabled}
                  onChange={(e) => handleFilterChange('arEnabled', e.target.checked)}
                />
                <span>AR Try-On Available</span>
              </label>
            </div>
          </motion.aside>

          {/* Products Grid */}
          <div className="products-content">
            {/* Sort Bar */}
            <div className="sort-bar">
              <select 
                value={filters.sort}
                onChange={(e) => handleFilterChange('sort', e.target.value)}
                className="sort-select"
              >
                <option value="newest">Newest First</option>
                <option value="price_low">Price: Low to High</option>
                <option value="price_high">Price: High to Low</option>
                <option value="rating">Highest Rated</option>
                <option value="popular">Most Popular</option>
              </select>
            </div>

            {/* Products Grid/List */}
            {isLoading ? (
              <div className="products-loading">
                <div className="spinner-large"></div>
                <p>Loading products...</p>
              </div>
            ) : products.length === 0 ? (
              <motion.div 
                className="products-empty"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <div className="empty-icon">üîç</div>
                <h3>No products found</h3>
                <p>Try adjusting your filters or search criteria</p>
                <Button variant="primary" onClick={clearFilters}>
                  Clear Filters
                </Button>
              </motion.div>
            ) : (
              <>
                <div className={`products-grid ${viewMode}`}>
                  {products.map((product, index) => (
                    <ProductCard 
                      key={product.id} 
                      product={product} 
                      index={index}
                    />
                  ))}
                </div>

                {/* Pagination */}
                {totalPages > 1 && (
                  <div className="pagination">
                    <Button
                      variant="secondary"
                      disabled={page === 1}
                      onClick={() => setPage(page - 1)}
                    >
                      ‚Üê Previous
                    </Button>
                    
                    <div className="pagination-pages">
                      {Array.from({ length: totalPages }, (_, i) => i + 1).map((p) => (
                        <button
                          key={p}
                          className={`page-btn ${p === page ? 'active' : ''}`}
                          onClick={() => setPage(p)}
                        >
                          {p}
                        </button>
                      ))}
                    </div>
                    
                    <Button
                      variant="secondary"
                      disabled={page === totalPages}
                      onClick={() => setPage(page + 1)}
                    >
                      Next ‚Üí
                    </Button>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  )
}

import { motion } from 'framer-motion'
import { Link } from 'react-router-dom'
import { useWishlistStore } from '../store/wishlistStore'
import { useCartStore } from '../store/cartStore'
import ProductCard from '../components/products/ProductCard'
import Button from '../components/common/Button'
import useToast from '../hooks/useToast'
import './Wishlist.css'

export default function Wishlist() {
  const { items, clearWishlist } = useWishlistStore()
  const { addItem } = useCartStore()
  const { toast } = useToast()

  const handleMoveToCart = (product) => {
    addItem(product)
    toast.success(`${product.name} added to cart!`)
  }

  const handleClearWishlist = () => {
    if (window.confirm('Are you sure you want to clear your wishlist?')) {
      clearWishlist()
      toast.info('Wishlist cleared')
    }
  }

  if (items.length === 0) {
    return (
      <div className="wishlist-empty">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <div className="empty-icon">üíù</div>
          <h2>Your wishlist is empty</h2>
          <p>Save your favorite items for later!</p>
          <Link to="/products">
            <Button variant="primary" size="large">
              Browse Products
            </Button>
          </Link>
        </motion.div>
      </div>
    )
  }

  return (
    <div className="wishlist-page">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <div className="wishlist-header">
            <div>
              <h1 className="font-display">My Wishlist</h1>
              <p className="wishlist-count">{items.length} items saved</p>
            </div>
            <button className="clear-wishlist-btn" onClick={handleClearWishlist}>
              Clear All
            </button>
          </div>

          <div className="wishlist-grid">
            {items.map((product, index) => (
              <div key={product.id} className="wishlist-item-wrapper">
                <ProductCard product={product} index={index} />
                <Button
                  variant="primary"
                  size="small"
                  onClick={() => handleMoveToCart(product)}
                  className="move-to-cart-btn"
                >
                  Add to Cart
                </Button>
              </div>
            ))}
          </div>
        </motion.div>
      </div>
    </div>
  )
}

import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import { authAPI } from '../services/api'

export const useAuthStore = create(
  persist(
    (set, get) => ({
      user: null,
      token: null,
      isAuthenticated: false,
      
      login: async (email, password) => {
        try {
          const response = await authAPI.login({ email, password })
          const { user, token } = response.data
          
          set({
            user,
            token,
            isAuthenticated: true
          })
          
          // Set token in axios defaults
          if (typeof window !== 'undefined') {
            localStorage.setItem('token', token)
          }
          
          return response
        } catch (error) {
          throw error
        }
      },
      
      register: async (name, email, password) => {
        try {
          const response = await authAPI.register({ name, email, password })
          const { user, token } = response.data
          
          set({
            user,
            token,
            isAuthenticated: true
          })
          
          // Set token in axios defaults
          if (typeof window !== 'undefined') {
            localStorage.setItem('token', token)
          }
          
          return response
        } catch (error) {
          throw error
        }
      },
      
      logout: () => {
        set({
          user: null,
          token: null,
          isAuthenticated: false
        })
        
        // Remove token from storage
        if (typeof window !== 'undefined') {
          localStorage.removeItem('token')
        }
      },
      
      updateUser: (userData) => {
        set({ user: { ...get().user, ...userData } })
      }
    }),
    {
      name: 'auth-storage'
    }
  )
)
